\begin{tabular}{c|c|c|c}
\hline
\multicolumn{2}{c|}{구분} & \multicolumn{2}{c}{기업 B} \\ \cline{3-4}
 \multicolumn{2}{c|}{}& b\textsubscript{1} & b\textsubscript{2} \\ \hline
\multirow{2}{*}{기업 A}& a\textsubscript{1} & (10, 7) & (15, 11) \\ \cline{2-4}
& a\textsubscript{2} & (9, 14) & (12, 13) \\ \hline
